const fs = require('fs');
const path = require('path');

// Read the JSON schema
const schemaPath = path.join(__dirname, '../src/schemas/listing-filters.json');
const schema = JSON.parse(fs.readFileSync(schemaPath, 'utf8'));

// Generate TypeScript content
const tsContent = `// Auto-generated schema from TypeScript types
// This file is generated by the build process - do not edit manually

export const listingFiltersSchema = ${JSON.stringify(schema, null, 2)} as const;

export default listingFiltersSchema;
`;

// Write the TypeScript file
const outputPath = path.join(__dirname, '../src/schemas/listing-filters.schema.ts');
fs.writeFileSync(outputPath, tsContent);

console.log('âœ… Generated TypeScript schema file:', outputPath);